import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
const [digimon, setDigimon] = useState("agumon");
const [digimonData, setDigimonData] = useState([]);
const [digimonType, setDigimonType] = useState("");



const handleChange = (e) => {
  setDigimon(e.target.value.toLowerCase ());
};
const handleSubmit = (e) => {
  e.preventdefault();
  getDigimon();
};
const getDigimon = async () => {
  const toArray = [];
  try {
const url = `https://digimon-api.vercel.app/api/digimon/${digimon}`;
const res = await axios.get(url);
toArray.push(res.data);
setDigimonType(res.data.types[0].type.name)
setDigimonData(toArray);

  } catch (e) {
    console.log(e)
  }
};
console.log(digimonData);

  return (
    <div className="App">
    <form onSubmit={handleSubmit}>
<label>
  <input type="text" onChange={handleChange} placeholder="Entrez le nom d'un Digimon" />
</label>
 </form>

    {digimonData.map((data) => {
  return(
    <div className="container">
<img src={data.sprites["front_default"]} />
<div className="divTable">
  <div className="divTableBody"></div>
  <div className="divTableRow">
    <div className="divTableCell">Level</div>
    <div className="divTableCell">{digimonType}</div>
  </div>
</div>

    </div>
  )
    } )
  } 
      </div>
  );
};

export default App;
